import React from 'react'
import {withStyles} from '@material-ui/core/styles'

import IconButton from '@material-ui/core/IconButton'
import AddIcon from '@material-ui/icons/Add'
import CloseIcon from '@material-ui/icons/Close'
import grey from '@material-ui/core/colors/grey'

import Tools from 'components/Tools'

class AppendRow extends React.Component {
    state = {
        isShowTools: false,
    }

    isShowTools = () => {        
        this.setState({isShowTools: true}, () => {
            this.props.onShowTools(true)
        })
    }

    hideTools = () => {
        this.setState({isShowTools: false}, () => {
            this.props.onShowTools(false)
        })
    }

    selectRowHandler = ({rowTypeId, id}) => {
        this.setState({isShowTools: false}, () => {
            this.props.selectRowHandler({rowTypeId, id})
        })
    }

    render() {
        const {
            classes, 
            isShowTools: isShowToolsProps,
            id,
        } = this.props
        const {isShowTools: isShowToolsState} = this.state

        if (isShowToolsState) {
            return (
                <div className={classes.append}>
                    <div className={classes.tools}>
                        <Tools selectRowHandler={val => this.selectRowHandler({rowTypeId: val, id: id})}/>  
                    </div>
                    <IconButton 
                        className={classes.icon}
                        onClick={this.hideTools}
                    >
                        <CloseIcon/>            
                    </IconButton>
                </div>
            )
        } else {
            return (
                <div className={classes.append}>
                    <IconButton 
                        className={classes.icon}
                        onClick={this.isShowTools}
                        disabled={isShowToolsProps}
                    >
                        <AddIcon/>            
                    </IconButton>
                </div>
            )
        }
    }    
}

const delay = 300
const styles = theme => ({
    append: {
        //display: 'flex',
        //justifyContent: 'center',
        //margin: '-30px auto',        
    },
    icon: {
        //margin: theme.spacing.unit * 2,
        margin: '-15px auto',
        display: 'block',
        backgroundColor: grey[400],
        color: theme.palette.background.paper,
        '&:hover': {
            //color: grey[800],
            backgroundColor: grey[500],
            transition: `background-color ${delay}ms`,
        },
    },
    tools: {
        width: '100%',
    }
})

export default withStyles(styles)(AppendRow)