import React from 'react'
import {
    string, 
    number, 
    func,
    node,
    bool,
    object,
} from 'prop-types'

import {withStyles} from '@material-ui/core/styles'
//import IconButton from '@material-ui/core/IconButton'
//import DeleteIcon from '@material-ui/icons/Delete'
//import Tooltip from '@material-ui/core/Tooltip'
import grey from '@material-ui/core/colors/grey'

import DeleteButton from 'components/DeleteButton'

class Row extends React.Component {
    static propTypes = {
        classes:          object.isRequired,
        mode:             string.isRequired,
        id:               number.isRequired,
        showToolsHandler: func.isRequired,
        isShowTools:      bool.isRequired,
        children:         node.isRequired,
        addHandler:       func.isRequired,
        deleteHandler:    func.isRequired,
    }

    state = {
        status: 'load'
    }

    componentDidMount() {
        const {mode} = this.props
        this.setState({status: mode})
    }

    componentWillReceiveProps(nextProps) {
        const oldMode = this.props.mode
        const newMode = nextProps.mode
        
        if (newMode !== oldMode) {
            this.setState({status: newMode})
        }
    }    

    deleteHandler = () => {
        const {id, deleteHandler} = this.props
        deleteHandler(id)
    }
    
    render() {
        const {status} = this.state
        const {
            children, 
            classes, 
            isShowTools,            
        } = this.props
        switch(status) {
            case 'load':
                return (
                    <div>
                        ShadowComponent
                    </div>
                )
                break
            case 'edit':
                return (
                    <div>
                        <DeleteButton
                            className={classes.delete}
                            deleteHandler={this.deleteHandler}
                            title='Удалить линию'
                        />                        
                        <div className={classes.root}>
                            {children}
                        </div>                        
                    </div>
                )
                break       
            case 'preview':
                return (
                    <div>                        
                        {children}
                    </div>
                )
                break            
        }        
    }
}

const styles = theme => ({
    root: {
        minHeight: 70,
        padding: '0px 20px',
        margin: 10,
    },
    delete: {        
        width: 36,
        height: 36,
        right: 0,
        position: 'absolute',
    }
})

export default withStyles(styles)(Row)